import { GetTopics } from '../services/posts.service'
import { useState, useEffect, useRef, useCallback } from 'react'

export const Topics = () => {

    const [topics,setTopics] = useState([]);
    const [load,setLoad] = useState(false);
    const [iterations, setIterations] = useState(20);

    const handleClickLoadMore = () => {
        setIterations((iterations+10));
    }

    function handleClickTopic(e){
        e.preventDefault();
        e.stopPropagation();
        console.log(e.target);
        let topicId = e.target.id;
        console.log(topicId);
        if(topicId === ''){
            topicId = ReactDOM.findDOMNode(e.target).parentNode.id;
        }
        ReactDOM.render(
            <div>
            </div>,
            document.getElementById('root')
        );
        setTimeout(() => {
            ReactDOM.render(
                <div>
                    <div>
                        <Banner />{topElement}
                    </div>
                    <div>
                        <AddPost topic={topicId} /><Posts topic={topicId}/>
                    </div>
                </div>
                    ,document.getElementById('root'));
            }, 100);
    }

    useEffect(() => {
        GetTopics()
        .then(data => {
            setTopics(data);
            setLoad(true);
        })
        .catch((err) => console.log(err)) 
    }, [])

    let topicsElement = <></>;

    let loadElement = (<div></div>);
    if(topics !== null){
        if(iterations < topics.length){
            loadElement = (<div id="load-more" onClick={handleClickLoadMore}>LOAD MORE POSTS</div>);
        }
    }
    if(load){
        topicsElement = (<ul>{topics.slice(0,iterations).map((top)=>
        <li className='topic' key={top.topic} id={top.topic} onClick={(e) => handleClickTopic(e)}>
            <p>{top.count}#{top.topic}</p>
        </li>
        )}</ul>);
    }

    return (
        <div>
            {topicsElement}
            {loadElement}
        </div>

    );
}
