import React from "react";
import './App.css';
class Posts extends React.Component {
   
    // Constructor 
    constructor(props) {
        super(props);
   
        this.state = {
            items: [],
            DataisLoaded: false
        };
    }
    
  render() {
    const { hits, isLoading } = this.state;

    if (isLoading) {
      return <p>Loading ...</p>;
    }
    let posts = Post();
    return posts;
    }

    GetPosts(){
        let url = "http://127.0.0.1:3000/api/posts/";
        /*if(id !== ""){
            url += id;
        }*/
        fetch(url).then(blob => blob.json())
        .then(data => {
            return data;
        })
        .catch((error) => {return error});
    }

     Post() {
        let posts = GetPosts();
        console.log(posts);
        if(posts !== undefined){
            return <div className="posts">
                <ul>
                    {posts.map((post,index) => (
                        <li key={`${post}-${index}`}>{ post }</li>
                        ))}
                </ul>
                </div>
        }
        else{
            return <div className="posts">
            <ul>
                <li>Charging</li>
            </ul>
            </div>
        }
    }
}

export default Posts
