import like from '../assets/Icons/like.png'
import nopic from '../assets/Icons/nopic.webp'
import { useState, useEffect, useRef, useCallback } from 'react'
import {GetComments,PostComment} from '../services/comments.service'

export const Comments = (id) => {
    const [isLoading, setLoading] = useState(false);
    const [comments, setComments] = useState([]);
    const [change, setChange] = useState(false);

      useEffect(() => {
        GetComments(id)
        .then(data => {console.log(data);setComments(data);})
        .catch((err) => console.log(err)) 
      }, [])

    if (isLoading) {
        return <h1>LOADING...</h1>
    }

    let commentsElement = (
        <ul>
        {comments.map((comment) =>
            <li className='comment' key={comment._id}>
                <img className='comment-profile-picture' src={nopic}></img>
                <div>
                    <p>{comment.author}~
                    {comment.text}
                    </p>
                </div>
            </li>)}
        </ul>
        );

    if(change){
        setChange(false);

        return (
        <ul>
        {comments.map((comment) =>
            <li className='comment' key={comment._id}>
                <img className='comment-profile-picture' src={nopic}></img>
                <div>
                    <p>{comment.author}~
                    {comment.text}
                    </p>
                </div>
            </li>)}
        </ul>
        );
    }
    if(comments.length === 0){
        commentsElement = (<p>No comments yet...</p>);
    }
    const refresh = () => {
        setChange(true);
        GetComments(id).then((data) =>{
            setComments(data);
        });
      }

     const handleSubmit = (postId,e) => {
        e.preventDefault();
        let text = e.target['input'].value;
        PostComment(postId,text);
    }

    return (
    <div className='commments-frame' onClick={(e) => {refresh()}}>
        <div className='comments-frame-content'>
            {commentsElement}
        </div>
        <div className='add-comment'>
                  <form onSubmit={(e) => {handleSubmit(post._id,e)}}>
                      <input name='input' className='add-comment-input'></input>
                      <button className='add-comment-button'>post</button>
                  </form>
              </div>
    </div>
    )
}
