import axios from "axios";

const BASE_URL = "http://127.0.0.1:3000/api/"

export const GetComments = (postId) => {
    const config = {
        headers: { Authorization: 'Bearer '+sessionStorage.getItem("token") }
    };
    /*const bodyParameters = {
       key: "value"
    };*/

    return axios.get(BASE_URL + 'posts/'+String(postId)+'/comments',config)
      .then((res) => res.data)
      .catch((err) => err)
}

export const addComment = (postId,comment) =>{
    const config = {
        headers: { Authorization: 'Bearer '+sessionStorage.getItem("token") }
    };
    const bodyParameters = {
       comment: comment
    };
    return axios.post(BASE_URL + 'posts/'+String(postId)+'/comments',config,bodyParameters)
      .then((res) => res.data)
      .catch((err) => err)
}
