import React, {useEffect, useState} from 'react';

export const GifSearch = () =>{

    const [gifs, setGifs] = useState([]);
    const [term, updateTerm] = useState('');

    useEffect(()=>{
        fetchGifs().then(()=>{
            //console.log(gifs);
            gifs.map((gif)=>{
                console.log(gif);
            })
        });
      
    },[term]);

    async function fetchGifs() {
        try {
        const API_KEY = 'EP7RKmRrQ1bMjBgJAFIR01e0FLIh7ds8';
        const BASE_URL = 'http://api.giphy.com/v1/gifs/search';
        fetch(`${BASE_URL}?api_key=${API_KEY}&q=${term}`).then((resJson)=>{
            resJson.json().then((res)=>{
                setGifs(res.data);
            });
        });
        } catch (error) {
        console.warn(error);
        }
    }

    function onGifSearch(e) {
        let newTerm = e.target.value;
        updateTerm(newTerm);
    }

    let gifListElement = (
    <ul>
        {gifs.map((gif)=>
            <li id={gif.id} key={gif.id}>
                <p>test</p>
                <p>{gif.images.original.url}</p>
            </li>
        )}
    </ul>
    );
    return (
        <div id='gif-search'>
            <input id='gif-search-input' onInput={(e) => onGifSearch(e)} width='20' height='20' multiple/>
            <p>{gifs.length}</p>
            <ul>
                {gifs.map((gif)=>
                    <li id={gif.id} key={gif.id}>
                        <img src={gif.images.fixed_height.url}></img>
                    </li>
                )}
            </ul>
        </div>
        /*<View style={styles.view}>
        <TextInput
            placeholder="Search Giphy"
            placeholderTextColor='#fff'
            style={styles.textInput}
            onChangeText={(text) => onGifSearch(text)}
        />
        <FlatList
            data={gifs}
            renderItem={({item}) => (
            <Image
                resizeMode='contain'
                style={styles.image}
                source={{uri: item.images.original.url}}
            />
            )}
        />
        </View>*/
    );
}
