import axios from "axios";

const BASE_URL = "http://127.0.0.1:3000/api/"

export const GetComments = (id) => {
    return axios.get(BASE_URL + 'posts/'+id+'/comments')
      .then((res) => res.data)
      .catch((err) => err)
}
