import axios from "axios";

const BASE_URL = "http://127.0.0.1:3000/api/"

export const GetProfile = () => {
    const options = {
        headers: { Authorization: 'Bearer '+sessionStorage.getItem("token") }
    };

    return axios.get(BASE_URL + 'posts/'+postId.id+'/comments',options)
      .then((res) => res.data)
      .catch((err) => err)
}
