import '../styles/AddPost.css'
import arrow from '../assets/Icons/arrow-right.webp'

export const AddPost = () => {
    return <div id="addpost-frame">
        <form id='addpost-form' onSubmit={(e) => handleClick(e)}>
            <input name='addpost-input'></input>
            <button name='addpost-submit'><img id='arrow-submit' src={arrow}></img></button>
        </form>
        </div>
}

function handleClick(e){
    let x = document.getElementById("addpost-form");
    e.target["addpost-input"].value = "";
    return;
}
