import save from '../assets/Icons/save.webp'
import pen from '../assets/Icons/pen.webp'
import {EditProfile} from '../services/profiles.service'
import { useEffect, useState } from 'react'

export const EditMainInfos = (profile) =>{
    const [state,setState] = useState(<div></div>);

    useEffect(setState(), []);
    const handleClickMainInfos = () => {
        let text = document.getElementById("firstname-lastname").innerText;
        let editform = 
        <form id='edit-main-infos'>
            <input onSubmit={(e) => handleSubmitMainInfos(e)}></input>
            <button><img src={save} width='10' height='10'></img></button>
        </form>;
       return editform;
      }
      const handleSubmitMainInfos = (e) => {
        e.preventDefault();
        let ProfileModified = profile;
        let mainInfos = e.target.value.split(' ');
        ProfileModified.firstname = mainInfos[0];
        ProfileModified.lastname = mainInfos[1];
        console.log(ProfileModified);
        EditProfile(ProfileModified)
        .then((data)=>{
          console.log(data);
        });
      }

    return (<button id='edit-main-infos-btn' onClick={() => handleClickMainInfos()}><img id='edit-main-infos-img' src={pen} width='20' height='20'></img></button>);
}
